sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Set the session flag to prevent recursion
    gs.getSession().putClientData('isSyncingComments', 'true');

    // Get the parent Request record
    var parentRequest = current.partner_onboarding_request.getRefRecord();
    if (parentRequest.isValidRecord()) {
        // Call the Script Include to perform the sync
        new x_1118046_partne_0.CommentSyncUtil().syncComments(current, parentRequest);
    }

    // IMPORTANT: Unset the flag after the operation is complete
    gs.getSession().putClientData('isSyncingComments', null);

})(current, previous);
